import React from "react";
import { Card } from "antd";
import PropType from "prop-types";
import {
  RetweetOutLined,
  HeartOutLined,
  MessageOutlined,
  EllipsisOutlined,
  Popover,
  ButtonGroup,
} from "@ant-design";

const PostCard = ({ post }) => {
  const { me } = useSelector((state) => state.user);
  const id = me && me.id;
  // 옵셔널 체이닝(optional chaining ) 연산자
  // const id = me?.id;
  // const id = useSelector((state) => state.user.me?.id);
  return (
    <>
      <Card
        cover={post.images[0] && <PostImages images={post.imges} />}
        action={[
          <RetweetOutLined key="retweet" />,
          <HeartOutLined ket="heart" />,
          <MessageOutlined key="comment" />,
          <Popover
            key="more"
            content={
              <ButtonGroup>
                <Button>수정</Button>
                <Button type="danger">삭제</Button>
                <Button>신고</Button>
              </ButtonGroup>
            }
          >
            <EllipsisOutlined />
          </Popover>,
        ]}
      >
        <Image />
        <Content />
        <Button></Button>
      </Card>
      <CommentForm />
      <Comments />
    </>
  );
};

PostCard.propType = {
  post: PropType.shape({
    id: PropType.number,
    User: PropType.object,
    content: PropType.string,
    createdAt: PropType.object,
    Comment: PropType.arrayOf(PropType.object),
    images: PropType.arrayOf(PropType.object),
  }).isRequired,
};

export default PostCard;
